type DataProps = {
  id: number;
  first_name: string;
  last_name: string;
  email: string;
};

const dataFields = ["id", "first_name", "last_name", "email"];

const App = () => {
  const [data, setData] = useState<DataProps[]>([]);

  const getData = async () => {
