      const jsonData = await res.json();
      setData(jsonData);
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    getData();
  }, []);

  if (data.length === 0) {
    return (
      <>
        <div className="text-3xl">No Data to display...</div>
      </>
    );
  }

  return (
    <>
      <Pagination data={data} allFields={dataFields} />
    </>
  );
};

export default App;`;

